(2, 'RSformPro Multipage example', '<div class="componentheading">{global:formtitle}</div>\n{error}\n<table border="0">\n	<!-- Do not remove this ID, it is used to identify the page so that the pagination script can work correctly -->\n	<tbody id="rsform_2_page_0">\n	<tr>\n		<td>{Header:caption}</td>\n		<td>{Header:body}<div class="formClr"></div>{Header:validation}</td>\n		<td>{Header:description}</td>\n	</tr>\n	<tr>\n		<td>{FullName:caption} (*)</td>\n		<td>{FullName:body}<div class="formClr"></div>{FullName:validation}</td>\n		<td>{FullName:description}</td>\n	</tr>\n	<tr>\n		<td>{Email:caption} (*)</td>\n		<td>{Email:body}<div class="formClr"></div>{Email:validation}</td>\n		<td>{Email:description}</td>\n	</tr>\n	<tr>\n		<td>&nbsp;</td>\n		<td>{Page1:body}</td>\n		<td>&nbsp;</td>\n	</tr>\n	</tbody>\n<!-- Do not remove this ID, it is used to identify the page so that the pagination script can work correctly -->\n	<tbody id="rsform_2_page_1">\n	<tr>\n		<td>{CompanyHeader:caption}</td>\n		<td>{CompanyHeader:body}<div class="formClr"></div>{CompanyHeader:validation}</td>\n		<td>{CompanyHeader:description}</td>\n	</tr>\n	<tr>\n		<td>{CompanySize:caption} (*)</td>\n		<td>{CompanySize:body}<div class="formClr"></div>{CompanySize:validation}</td>\n		<td>{CompanySize:description}</td>\n	</tr>\n	<tr>\n		<td>{Position:caption} (*)</td>\n		<td>{Position:body}<div class="formClr"></div>{Position:validation}</td>\n		<td>{Position:description}</td>\n	</tr>\n	<tr>\n		<td>&nbsp;</td>\n		<td>{Page2:body}</td>\n		<td>&nbsp;</td>\n	</tr>\n	</tbody>\n<!-- Do not remove this ID, it is used to identify the page so that the pagination script can work correctly -->\n	<tbody id="rsform_2_page_2">\n	<tr>\n		<td>{ContactHeader:caption}</td>\n		<td>{ContactHeader:body}<div class="formClr"></div>{ContactHeader:validation}</td>\n		<td>{ContactHeader:description}</td>\n	</tr>\n	<tr>\n		<td>{ContactBy:caption}</td>\n		<td>{ContactBy:body}<div class="formClr"></div>{ContactBy:validation}</td>\n		<td>{ContactBy:description}</td>\n	</tr>\n	<tr>\n		<td>{ContactWhen:caption} (*)</td>\n		<td>{ContactWhen:body}<div class="formClr"></div>{ContactWhen:validation}</td>\n		<td>{ContactWhen:description}</td>\n	</tr>\n	<tr>\n		<td>{Submit:caption}</td>\n		<td>{Submit:body}<div class="formClr"></div>{Submit:validation}</td>\n		<td>{Submit:description}</td>\n	</tr>\n	<tr>\n		<td>{Footer:caption}</td>\n		<td>{Footer:body}<div class="formClr"></div>{Footer:validation}</td>\n		<td>{Footer:description}</td>\n	</tr>\n	</tbody>\n</table>\n', 'inline', 1, '', '', 'RSForm! Pro Multipage example', 1, '', '', 0, '<p>Dear {FullName:value},</p><p> thank you for your submission. One of our staff members will contact you by  {ContactBy:value} as soon as possible.</p>', '<p>Dear {FullName:value},</p><p> we received your contact request. Someone will get back to you by {ContactBy:value} soon. </p>', '{Email:value}', '', '', 'your@email.com', '', 'Your Company', 'Contact confirmation', 1, 0, '', '<p>Customize this e-mail also. You will receive it as administrator. </p><p>{FullName:caption}:{FullName:value}<br />\n{Email:caption}:{Email:value}<br />\n{CompanySize:caption}:{CompanySize:value}<br />\n{Position:caption}:{Position:value}<br />\n{ContactBy:caption}:{ContactBy:value}<br />\n{ContactWhen:caption}:{ContactWhen:value}</p>', 'youradminemail@email.com', '', '', '{Email:value}', '', 'Your Company', 'Contact', 1, '', '', '', '', '', 0, 'This is the meta description of your form. You can use it for SEO purposes.', 'rsform, contact, form, joomla', '(*)', '<p class="formRed">Please complete all required fields!</p>', ', ', 1);
