INSERT INTO `#__content` VALUES (22,'Discover','discover','','{xhub:include type=\"stylesheet\" filename=\"pages/discover.css\"}\r\n<div class=\"firstset\">\r\n<div class=\"four columns first\">\r\n		<h2>Do More</h2>\r\n</div>\r\n<div class=\"four columns second third fourth\">\r\n	<div class=\"three columns first\">\r\n		<div class=\"presentation\">\r\n			<h3><a href=\"/resources\">Resources</a></h3>\r\n			<p>Find the latest cutting-edge research in our <a href=\"/resources\">resources</a>.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"three columns second\">\r\n		<div class=\"quote\">\r\n			<h3><a href=\"/citations\">Citations</a></h3>\r\n			<p>See who has <a href=\"/citations\">cited</a> our content in their work.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"three columns third\">\r\n		<div class=\"tag\">\r\n			<h3><a href=\"/tags\">Tags</a></h3>\r\n			<p>Explore all our content through <a href=\"/tags\">tags</a> or even tag content yourself.</p>\r\n		</div>\r\n	</div>\r\n	\r\n	<div class=\"clear\"></div>\r\n	\r\n	<div class=\"three columns first\">\r\n		<div class=\"blog\">\r\n			<h3><a href=\"/blog\">Blog</a></h3>\r\n			<p>Read the <a href=\"/blog\">latest entry</a> or browse the archive for articles of interest.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"three columns second\">\r\n		<div class=\"wiki\">\r\n			<h3><a href=\"/wiki\">Wiki</a></h3>\r\n			<p>Browse our user-generated <a href=\"/wiki\">wiki pages</a> or write your own.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"three columns third\">\r\n		<div class=\"feedback\">\r\n			<h3><a href=\"/feedback\">Feedback</a></h3>\r\n			<p>Like something? Having trouble? <a href=\"/feedback\">Let us know what you think!</a></p>\r\n		</div>\r\n	</div>\r\n</div>\r\n</div><!-- / .firstset -->\r\n\r\n<div class=\"clear\"></div>\r\n\r\n<div class=\"four columns first\">\r\n	<h2>Services</h2>\r\n</div>\r\n<div class=\"four columns second\">\r\n	<div class=\"contribute\">\r\n		<h3><a href=\"/resources/new\">Upload</a></h3>\r\n		<p><a href=\"/resources/new\">Publish</a> your own tools, seminars, and other content on this site.</p>\r\n	</div>\r\n</div>\r\n<div class=\"four columns third\">\r\n	<div class=\"tool\">\r\n		<h3><a href=\"/tools\">Tool Forge</a></h3>\r\n		<p>The <a href=\"/tools\">development area</a> for simulation tools. Sign up and manage your own software project!</p>\r\n	</div>\r\n</div>\r\n<div class=\"four columns fourth\">\r\n	<div class=\"cart\">\r\n		<h3><a href=\"/store\">Store</a></h3>\r\n		<p><a href=\"/store\">Purchase items</a> such as t-shirts using points you earn by helping out.</p>\r\n	</div>\r\n</div>\r\n\r\n<div class=\"clear\"></div>\r\n\r\n<div class=\"four columns first\">\r\n	<h2>What\'s Happening</h2>\r\n</div>\r\n<div class=\"four columns second\">\r\n	<div class=\"event\">\r\n		<h3><a href=\"/events\">Events</a></h3>\r\n		<p>Find information about the many upcoming <a href=\"/events\">public meetings and scientific symposia</a>.</p>\r\n	</div>\r\n</div>\r\n<div class=\"four columns third\">\r\n	<div class=\"new\">\r\n		<h3><a href=\"/whatsnew\">What\'s New</a></h3>\r\n		<p>Find the latest content posted on the site with our <a href=\"/whatsnew\">What\'s New</a> section.</p>\r\n	</div>\r\n</div>\r\n<div class=\"four columns fourth\">\r\n	<div class=\"poll\">\r\n		<h3><a href=\"/poll\">Poll</a></h3>\r\n		<p>Respond to our poll questions and <a href=\"/poll\">see what everyone else is thinking</a>.</p>\r\n	</div>\r\n</div>\r\n\r\n<div class=\"clear\"></div>','',1,0,0,0,'2010-03-27 16:47:23',62,'','2012-09-07 16:48:44',1008,0,'0000-00-00 00:00:00','2010-03-27 16:47:23','0000-00-00 00:00:00','','','show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',12,0,4,'','',0,142,'robots=\nauthor=');
INSERT INTO `#__content` VALUES (32,'Congratulations','congratulations','','{xhub:include type=\"stylesheet\" filename=\"pages/congrats.css\"}\r\n\r\n<div id=\"features\">\r\n<div class=\"wrap\">\r\n	<div class=\"three columns first\">\r\n<div class=\"use\">\r\n		<h3>Utilize</h3>\r\n		<p>View tutorials and read about how to use the numerous features of a HUB. We show you how to add content, customize your dashboard, create groups, and more.</p>\r\n		<p class=\"btn-container\"><a class=\"btn\" href=\"http://hubzero.org/documentation/1.0.0/users\" rel=\"external\">User documentation</a></p>\r\n</div>\r\n	</div>\r\n	<div class=\"three columns second\">\r\n<div class=\"manage\">\r\n		<h3>Manage</h3>\r\n		<p>Read the manual for managing the content and functionality of a HUB. It progresses step-by-step through various common tasks and familiarizes you with the administrative interface.</p>\r\n		<p class=\"btn-container\"><a class=\"btn\" href=\"http://hubzero.org/documentation/1.0.0/managers\" rel=\"external\">Manager documentation</a></p>\r\n</div>\r\n	</div>\r\n	<div class=\"three columns third\">\r\n<div class=\"customize\">\r\n		<h3>Extend</h3>\r\n		<p>Build your own extensions and discover how to extend or tailor the existing ones to your needs. We try to guide you through the creation steps and provide examples for download.</p>\r\n		<p class=\"btn-container\"><a class=\"btn\" href=\"http://hubzero.org/documentation/1.0.0/webdevs\" rel=\"external\">Developer documentation</a></p>\r\n</div>\r\n	</div>\r\n	<div class=\"clear\"></div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"highlight-section\">\r\n	<p>\r\n		Ready to go? Jump to the <a rel=\"external\" href=\"/administrator\">administration</a> or learn how to <a href=\"http://hubzero.org\" rel=\"external\">change this page</a>.\r\n	</p>\r\n</div><!-- / .highlight-section -->\r\n\r\n<div class=\"support-section\">\r\n<div class=\"wrap\">\r\n	<div class=\"four columns first\">\r\n		<h2>Support</h2>\r\n		<p>\r\n			If you need help or support while developing your HUB, reach out to us.\r\n		</p>\r\n	</div>\r\n	<div class=\"four columns second third fourth\">\r\n		<div class=\"two columns first\">\r\n			<h3>I have an idea!</h3>\r\n			<p>\r\n				Think something can be done better or is missing? Post your <a href=\"http://hubzero.org/wishlist\" rel=\"external\">ideas or feature requests</a>. We\'d love to hear from you.\r\n			</p>\r\n		</div>\r\n		<div class=\"two columns second\">\r\n			<h3>I have an error!</h3>\r\n			<p>\r\n				We continually test our code and strive for a smooth, error-free experience but, sadly, we can make mistakes. If you found a bug, <a href=\"http://hubzero.org/support/ticket/new\" rel=\"external\">let us know</a>.\r\n			</p>\r\n		</div>\r\n		<div class=\"clear\"></div>\r\n	</div>\r\n	<div class=\"clear\"></div>\r\n</div>\r\n</div><!-- / .inner -->\r\n\r\n<div class=\"blocks-section wrap\">\r\n	<div class=\"four columns first\">\r\n		<h2>Community</h2>\r\n		<p>\r\n			Connect with the growing HUB community and see what your peers have to say.\r\n		</p>\r\n	</div>\r\n	<div class=\"four columns second third fourth\">\r\n		<div class=\"two columns first\">\r\n			<h3>Groups</h3>\r\n			<p>\r\n				Members working together. <a rel=\"external\" href=\"http://hubzero.org/groups\">Groups</a> allow <a rel=\"external\" href=\"http://hubzero.org/members\">members</a> with similar interests to get in touch with each other and collaborate on projects.\r\n			</p>\r\n		</div>\r\n		<div class=\"two columns second\">\r\n			<h3>Wiki</h3>\r\n			<p>\r\n				The <a rel=\"external\" href=\"http://hubzero.org/wiki\">wiki</a> is a space where you can share work, ideas, pictures, links, and media &mdash; and just about anything else you can think of.\r\n			</p>\r\n		</div>\r\n		<div class=\"clear\"></div>\r\n\r\n		<div class=\"two columns first\">\r\n			<h3>Questions &amp; Answers</h3>\r\n			<p>\r\n				This is where you can <a rel=\"external\" href=\"http://hubzero.org/answers\">ask questions</a>, get answers, and help others in the community.\r\n			</p>\r\n		</div>\r\n		<div class=\"two columns second\">\r\n			<h3>Forums</h3>\r\n			<p>\r\n				A place for you to start a <a rel=\"external\" href=\"http://hubzero.org/forum\">discussion</a> or find out what everyone is talking about.\r\n			</p>\r\n		</div>\r\n		<div class=\"clear\"></div>\r\n	</div>\r\n	<div class=\"clear\"></div>\r\n</div><!-- / .inner -->','',1,0,0,0,'2012-09-07 16:55:44',1008,'','2012-09-07 19:16:06',1008,0,'0000-00-00 00:00:00','2012-09-07 16:55:44','0000-00-00 00:00:00','','','show_title=0\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=',2,0,2,'','',0,6,'robots=\nauthor=');
INSERT INTO `#__content` VALUES (27,'Privacy Policy','privacy','','<p>We respect your privacy. We will never sell your e-mail address or other personally identifiable information to third parties, or make that information publicly available without your permission.</p>\r\n<p>We are a scientific project and we provide a free service, so we do track your activities on this site.  We combine your usage metrics with others to create an aggregate picture of how the site is being used, and we report <a href=\"usage\">that usage</a> publicly on our site and send similar quarterly reports to our program sponsors.  However, such usage reports never contain detailed information about your activities as an individual without your prior consent.</p>\r\n<p>From time to time, we work with other other projects that seek to study social interactions on the site.  We may share your activities with those groups, but they are bound by a Memorandum of Understanding to follow our policies and respect your privacy in the same way that we do.  We may contact you on their behalf and ask you to participate in their study, but your participation is voluntary, and your information will not be publicly released without your prior consent.</p>\r\n<p>We reserve the right to contact you to request additional information or to keep you updated on changes to {xhub:getcfg hubShortURL}. You may opt out of receiving newsletters and other non-essential communications by updating the settings in your account profile.</p>\r\n<p>All resources, wiki articles, blogs, questions, answers, or other information posted or stored on {xhub:getcfg hubShortURL} is potentially available to all other users of {xhub:getcfg hubShortURL}.  We do our best to restrict access to source code and to other private materials shared within groups.  However, we are a research project and our software is constantly changing.  We disclaim any responsibility or capability for the preservation of confidentiality of any information posted on this site. <em>Do not post or store information on {xhub:getcfg hubShortURL} if you expect to or are obligated to protect the confidentiality of that information.</em></p>\r\n<h3>Changes to policy</h3>\r\n<p>We may occasionally update our Privacy Policy to reflect changes in our practices and services. If we make material changes in the way we collect, use, or share your personal information, we will notify you and/or prominently post notices of the changes on our website.</p>','',1,3,0,3,'2012-05-02 12:15:06',62,'','2012-05-02 12:20:30',62,0,'0000-00-00 00:00:00','2012-05-02 12:15:06','0000-00-00 00:00:00','','','show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=',2,0,4,'','',0,0,'robots=\nauthor=');
INSERT INTO `#__content` VALUES (28,'Abuse Policy','abuse','','<p>\r\nYou are responsible at all times for using {xhub:getcfg hubShortURL} in a manner that is legal, ethical, and not to the detriment of others.  Once you have created an account, you are allowed to post materials and participate in other social networking features available on this site.  All members are encouraged to help moderate content and click on \"report abuse\" links to report any material deemed offensive or inappropriate.  If your posted material is reported as abusive, it will be immediately withdrawn and subsequently reviewed by a site administrator.  If the material is judged to be appropriate, it will be restored.  Otherwise, it will be removed from the site, and depending on the inappropriate nature of the content, your account may be terminated.\r\n</p><p>\r\nAll users must recognize that system resources on {xhub:getcfg hubShortURL} are limited.  Quotas for disk storage, job submissions, and other limited resources are strictly enforced. By using {xhub:getcfg hubShortURL}, you agree to delete unused files and acknowledge that if you fail to do so, the files may be deleted for you.  You may request an increase in system quotas, and you may use points to pay for premium services, but you may not circumvent the policies put in place to manage resources.\r\n</p><p>\r\n<em>Creating multiple accounts or otherwise gaming the system in order to earn extra points, to increase your disk quota, or to gain an unfair share of other system resources is strictly prohibited and may lead to termination of your account.</em></p>\r\n<p>\r\nBy using {xhub:getcfg hubShortURL}, you agree that:\r\n</p>\r\n<ul>\r\n <li>You will not use {xhub:getcfg hubShortURL} to commit a criminal offense or to encourage others to conduct what would constitute a criminal offense or give rise to a civil liability;</li>\r\n <li>You will not violate any applicable law;</li>\r\n <li>You will not post or transmit any unlawful, threatening, libelous, harassing, defamatory, vulgar, obscene, pornographic, profane, or otherwise objectionable content;</li>\r\n <li>You will not use {xhub:getcfg hubShortURL} to impersonate other parties or entities;</li>\r\n <li>You will not use {xhub:getcfg hubShortURL} to upload any content that contains a software virus, \"Trojan Horse\" or any other computer code, files, or programs that may alter, damage, or interrupt the functionality of {xhub:getcfg hubShortURL} or the hardware or software of any other person who accesses {xhub:getcfg hubShortURL};</li>\r\n <li>You will not post any material that infringes or violates the intellectual property rights of another;</li>\r\n <li>You will not redistribute resource materials posted on {xhub:getcfg hubShortURL} or create derivative works unless it is allowed by the license terms posted for a resource;</li>\r\n <li>You will not alter, damage, or delete any content posted on {xhub:getcfg hubShortURL};</li>\r\n <li>You will not claim a relationship with or speak for any business, association, or other organization for which you are not authorized to claim such a relationship;</li>\r\n <li>You will not post or transmit any unsolicited advertising, promotional materials, or other forms of solicitation;</li>\r\n <li>You will not use email addresses obtained or accessed through {xhub:getcfg hubShortURL} to transmit the same or substantially similar unsolicited message to multiple recipients without a valid purpose specifically related to the research and education mission of the {xhub:getcfg hubShortURL};</li>\r\n</ul>','',1,3,0,3,'2012-05-02 12:19:12',62,'','2012-05-02 12:20:14',62,0,'0000-00-00 00:00:00','2012-05-02 12:19:12','0000-00-00 00:00:00','','','show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=',2,0,3,'','',0,0,'robots=\nauthor=');
INSERT INTO `#__content` VALUES (29,'Legal','legal','','<h3>Policies</h3>\r\n<p>Review {xhub:getcfg hubShortURL}\'s <a href=\"/legal/privacy\">Privacy Policy</a> or <a href=\"/legal/abuse\">Abuse Policy</a>.</p>\r\n\r\n<h3>Legal Contacts</h3>\r\n<div class=\"two columns first\" id=\"getintouch\">\r\n	{xhub:module position=\"contact\"}\r\n</div><!-- / .two columns first -->\r\n<div class=\"two columns second\">\r\n	<div class=\"vcard\">	\r\n		<address class=\"adr\">\r\n			<span class=\"fn org\">{Your Organization}</span><br />\r\n			<span class=\"street-address\">{Street Address}</span><br />\r\n			<span class=\"locality\">{City}</span>, <span class=\"region\">{Region/Stqte}</span> <span class=\"postal-code\">{Postal Code}</span><br />\r\n			<span class=\"country-name\">{Country}</span>\r\n		</address>\r\n		<p>\r\n			<span class=\"tel\"><span class=\"type\">Phone</span> +{Telephone}</span>\r\n		</p>\r\n	</div><!-- / .vcard -->\r\n</div><!-- / .two columns second -->','',1,3,0,3,'2012-05-02 12:21:09',62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00','2012-05-02 12:21:09','0000-00-00 00:00:00','','','show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=',1,0,2,'','',0,2,'robots=\nauthor=');
INSERT INTO `#__content` VALUES (30,'HUBzero Accessibility Statement','accessibility','','<div id=\"getintouch\" style=\"width:350px; float:right; margin:0px 0px 15px 25px; border: #cccccc 1px solid; padding:0px 10px 10px 10px;\">\r\n	{xhub:module position=\"accessibilitycontact\"}\r\n</div>\r\n\r\n<div>\r\n<h3>An Open Commitment</h3>\r\n<p>HUBzero<sup>&reg;</sup> is committed to providing a website that is accessible to the widest possible audience, regardless of technology or ability.</p>\r\n<p>We are actively working to increase the accessibility and usability of our website and in doing so adhere to many of the available standards and guidelines.</p>\r\n\r\n<h3>Guidelines, Standards and Testing</h3>\r\n<p>HUBzero<sup>&reg;</sup> has been developed using the following:</p>\r\n<dl>\r\n	<dt>Section 508</dt>\r\n	<dd>\r\n		<p>This website endeavors to meet or exceed the requirements of <a href=\"http://www.access-board.gov/508.htm\">Section 508 of the Rehabilitation Act</a>.</p>\r\n	</dd>\r\n	\r\n	<dt>WCAG 2.0</dt>\r\n	<dd>\r\n		<p>This website endeavors to conform to level AA standards, and where possible strive for AAA, as defined by <a href=\"http://www.w3.org/TR/WCAG20\" rel=\"external\">W3C\'s Web Content Accessibility Guidelines (WCAG) 2.0</a>. These guidelines provide web authors and designers with recommendations to help provide website content that is accessible to all users.</p>\r\n	</dd>\r\n	\r\n	<dt>Coding Standards</dt>\r\n	<dd>\r\n		<p>Pages on this site have been authored to be compliant with current W3C standards for <a href=\"http://www.w3.org/TR/xhtml1/\" rel=\"external\">HTML (XHTML 1.0 Strict)</a> and <a href=\"http://www.w3.org/TR/CSS21/\" rel=\"external\">CSS</a>. Standards-compliant code helps to ensure that visitors using assistive technologies (such as screen readers) are able to access the site contents.</p>\r\n	</dd>\r\n	\r\n	<dt>Additional Testing</dt>\r\n	<dd>\r\n		<p></p>\r\n		<p>In addition to reviewing for compliance with the standards noted above, we periodically test with such technologies as the <a href=\"http://wave.webaim.org/\" rel=\"external\">Web Accessibility Evaluation Tool</a>, <a href=\"http://achecker.ca/checker/index.php\" rel=\"external\">AChecker</a>, and try to test with a range of assistive technologies (e.g., JAWS and WindowEyes screen readers). We also appreciate any input from third parties and assistive technology users from the community.</p>\r\n	</dd>\r\n</dl>\r\n\r\n<h3>Contributed Content</h3>\r\n<p>While we make every effort to make sure that all pages designed and maintained by us are fully accessible, the accessibility of some contributed content is most often beyond our control.</p>\r\n</div>','',1,3,0,3,'2012-05-02 12:28:16',62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00','2012-05-02 12:28:16','0000-00-00 00:00:00','','','show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=',1,0,1,'','',0,0,'robots=\nauthor=');
INSERT INTO `#__content` VALUES (31,'Community','community','','{xhub:include type=\"stylesheet\" filename=\"pages/community.css\"}\r\n<div class=\"blocks-section\">\r\n	<div class=\"two columns first\">\r\n		<div class=\"member\">\r\n			<h3><a href=\"/members\">Members</a></h3>\r\n			<p>Browse the <a href=\"/members\">profiles</a> of people who have joined this site. Find members with similar <a href=\"/tags\">interests</a>.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"two columns second\">\r\n		<div class=\"group\">\r\n			<h3><a href=\"/groups\">Groups</a></h3>\r\n			<p>Members working together. Explore our <a href=\"/groups\">groups</a> and get in touch with <a href=\"/members\">members</a> with similar interests.</p>\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"clear\"></div>\r\n\r\n	<div class=\"two columns first\">\r\n		<div class=\"feedback\">\r\n			<h3><a href=\"/answers\">Questions &amp; Answers</a></h3>\r\n			<p><a href=\"/answers\">Ask questions</a>, get answers, and help others.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"two columns second\">\r\n		<div class=\"forum\">\r\n			<h3><a href=\"/forum\">Forum</a></h3>\r\n			<p>A place for you to start a <a href=\"/forum\">discussion</a> or find out what everyone is talking about.</p>\r\n		</div>\r\n	</div>\r\n	\r\n	<div class=\"clear\"></div>\r\n</div><!-- / .firstset -->\r\n\r\n<div class=\"clear\"></div>','',1,0,0,0,'2012-09-07 16:33:42',1008,'','2012-09-07 16:45:41',1008,0,'0000-00-00 00:00:00','2012-09-07 16:33:42','0000-00-00 00:00:00','','','show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=',3,0,3,'','',0,4,'robots=\nauthor=');
