INSERT INTO `#__content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES (1,'Home','home','','{xhub:include type=\"stylesheet\" filename=\"home.css\"}\r\n<div class=\"firstset\">\r\n				<div class=\"three columns first\">\r\n                                   <div id=\"find_resources\">\r\n					{xhub:module position=\"frontResources\" style=\"-2\"}\r\n                                   </div>\r\n				</div>\r\n				<div class=\"three columns second\">\r\n                                   <div id=\"spotlight\">\r\n					{xhub:module position=\"spotlight\" style=\"-2\"}\r\n                                   </div>\r\n				</div>\r\n				<div class=\"three columns third\">\r\n<h3>Get Involved</h3>\r\n<p>\r\n<ul>\r\n<li class=\"contribute\"><a href=\"contribute\">Upload Content</a><br />Publish your own materials</li>\r\n<li class=\"group\"><a href=\"groups\">Form working groups</a><br />Share things privately with colleagues</li>\r\n<li class=\"poll\"><a href=\"xpoll\">Take a Poll</a><br />{xhub:module position=\"polltitle\"}</li>\r\n<li class=\"feedback\"><a href=\"feedback\">Give us Feedback</a><br />Success story? Suggestions?</li>\r\n<li class=\"contact\"><a href=\"about/contact\">Contact Us</a><br />How to reach us</li>\r\n</ul>\r\n</p>\r\n{xhub:module position=\"frontFarRight\" style=\"-2\"}\r\n				</div>\r\n</div>\r\n<div class=\"clear\"></div>			\r\n<div class=\"lastset\">\r\n				<div class=\"three columns first\">\r\n					<div class=\"note\">\r\n{xhub:module position=\"frontLeft\" style=\"-2\"}\r\n					</div>\r\n				</div>\r\n				<div class=\"three columns second\">\r\n					<div class=\"note\">\r\n{xhub:module position=\"frontRight\" style=\"-2\"}\r\n					</div>\r\n				</div>\r\n				<div class=\"three columns third\">\r\n                                    <div class=\"note\">\r\n{xhub:module position=\"frontSide\" style=\"-2\"}	\r\n                                    </div>			\r\n				</div>\r\n</div>			\r\n<div class=\"clear\"></div>\r\n\r\n<!--\r\n<div class=\"four columns first\">\r\n<h3><a href=\"resources/tools\">Tools</a></h3>\r\n<p class=\"tool\">We offer a wide variety of simulation tools available for use right in your browser! No installing or configurtion needed. <a href=\"resources/tools\">More ...</a></p>\r\n<h3>Collaboration</h3>\r\n<ul>\r\n<li class=\"group\"><a href=\"groups\">User Groups</a><br />Share with your colleagues</li>\r\n<li class=\"webmeetings\"><a href=\"collaboration\">Collaboration</a><br />Other ways to collaborate</li>\r\n</ul>\r\n</div>\r\n<div class=\"four columns second\">\r\n<h3><a href=\"resources\">Resources</a></h3>\r\n<ul>\r\n<li class=\"animation\"><a href=\"resources/animations/\">Animations</a></li>\r\n<li class=\"course\"><a href=\"resources/courses/\">Courses</a></li>\r\n<li class=\"download\"><a href=\"resources/downloads/\">Downloads</a></li>\r\n<li class=\"learningmodule\"><a href=\"resources/learningmodules/\">Learning Modules</a></li>\r\n<li class=\"note\"><a href=\"resources/notes/\">Notes</a></li>\r\n<li class=\"presentation\"><a href=\"resources/onlinepresentations/\">Online Presentations</a></li>\r\n<li class=\"publication\"><a href=\"resources/publications/\">Publications</a></li>\r\n<li class=\"series\"><a href=\"resources/series/\">Series</a></li>\r\n<li class=\"teachingmaterial\"><a href=\"resources/teachingmaterials/\">Teaching Materials</a></li>\r\n<li class=\"workshop\"><a href=\"resources/workshops/\">Workshops</a></li>\r\n</ul>\r\n</div>\r\n<div class=\"four columns third\">\r\n<h3><a href=\"tags\">Tags</a></h3>\r\n<p class=\"tag\">A tag is like a subject, keyword, or category. You can find content related to a particular subject by clicking a tag.</p>\r\n<ol class=\"tags\">\r\n<li><a href=\"tags/courselecture\">course lecture</a></li>\r\n<li><a href=\"tags/cyberinfrastructure\">cyberinfrastructure</a></li>\r\n<li><a href=\"tags/experiments\">experiments</a></li>\r\n<li><a href=\"tags/researchseminar\">research seminar</a></li>\r\n</ol></div>\r\n<div class=\"four columns fourth\">\r\n<h3><a href=\"contribute\">Contribute</a></h3>\r\n<ul>\r\n<li class=\"contribute\"><a href=\"contribute\">Contribute Content</a><br />Upload your own materials</li>\r\n<li class=\"feedback\"><a href=\"feedback\">Give us Feedback</a><br />Success story? Suggestions?</li>\r\n<li class=\"poll\"><a href=\"feedback/poll\">Take a Poll</a><br />{xhub:module position=\"polltitle\"}</li>\r\n<li class=\"contact\"><a href=\"about/contact\">Contact Us</a><br />How to get ahold of us</li>\r\n</ul>\r\n</div>\r\n<div class=\"clear\"></div>\r\n<div class=\"three columns first\">{xhub:module position=\"frontLeft\" style=\"-2\"}</div>\r\n<div class=\"three columns second\">{xhub:module position=\"frontRight\" style=\"-1\"}</div>\r\n<div class=\"three columns third\">{xhub:module position=\"feature\" style=\"-2\"}</div>\r\n-->','',1,0,0,0,'NOW()',62,'','NOW()',62,0,'0000-00-00 00:00:00','NOW()','0000-00-00 00:00:00','','','show_title=0\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',6,0,1,'','',0,180,'robots=\nauthor=');
INSERT INTO `#__content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES (2,'Our Cyberinfrastructure','hubzero','','<p>\r\n<img src=\"{xhub:templatedir}/images/hubzero-logo.png\" align=\"right\"/>\r\nOur web site is powered by the <a href=\"http://www.hubzero.org\">HUBzero</a>\r\nsoftware developed at <a href=\"http://www.purdue.edu\">Purdue University</a>.\r\nHUBzero was specifically designed to help a scientific community share\r\nresources and work together with one another.  Users can upload their own\r\ncontent--including tutorials, courses, publications, and animations--and\r\nshare them with the rest of the community.  But each hub is more than\r\njust a repository of information.  It is a place where researchers and\r\neducators can share data and simulation tools online.  Users can launch\r\nsimulations and post-process results with an ordinary web browser--without\r\nhaving to download, compile, or install any code.  The tools they access\r\nare not just web forms, but powerful graphical tools that support visualization\r\nand comparison of results.\r\n</p>\r\n<h3>How Does a Hub Differ From a Web Site?</h3>\r\n<p>\r\nAt its core, a hub is a web site built with many familiar open source\r\npackages--the Linux operating system, an Apache web server, a MySQL\r\ndatabase, PHP web scripting, and the Joomla content management\r\nsystem.  The HUBzero software builds upon that infrastructure to create\r\nan environment in which researchers, educators, and students can access\r\nsimulation tools and share information.  Specifically, we define a \"hub\"\r\nas a web-based collaboration environment with the following features:\r\n<ul>\r\n	<li>Interactive simulation tools, hosted on the hub cluster and delivered to your browser</li>\r\n	<li>Simulation tool development area, including source code control and bug tracking</li>\r\n	<li>Animated presentations delivered in a light-weight, Flash-based format</li>\r\n	<li>Mechanism for uploading and sharing resources</li>\r\n	<li>5-star ratings and user feedback for resources</li>\r\n	<li>User support area, with question-and-answer forum</li>\r\n	<li>Statistics about users and usage patterns</li>\r\n</ul>\r\n</p><p>\r\nYou can use this software to <a href=\"http://www.hubzero.org/getstarted\">start a hub</a> for your own scientific community.  Visit <a href=\"http://www.hubzero.org\">HUBzero.org</a> to learn more!\r\n</p>','',1,1,0,1,'NOW()',62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00','NOW()','0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',2,0,1,'','',0,12,'robots=\nauthor=');
INSERT INTO `#__content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES (3,'About Us','about','','<p class=\"warning\">\r\nEdit this page and fill in information about your project.  Think of\r\nthis page as the front page of a brochure.  Describe your overall\r\nproject and its goals in simple terms.  The following text is just\r\na suggestion for the kinds of things you might see on this page.\r\n</p>\r\n\r\n<h3>Our Mission</h3>\r\n\r\n<p>The Your Organization (YORG) is funded by <i>Your Funding Organization</i>\r\nwith a mission to <i>change the world</i>.</p>\r\n\r\n<h3>What is {xhub:getcfg hubShortName}?</h3>\r\n<p>\r\nThis web site is a resource for research, education and collaboration\r\nin <i>your science area</i>.  It hosts\r\n<a href=\"resources/\">various resources</a> which will help you learn\r\nabout <i>your science area</i>, including\r\n<a href=\"/resources/presentations\">Online Presentations</a>,\r\n<a href=\"/resources/courses\">Courses</a>,\r\n<a href=\"/resources/learningmodules\">Learning Modules</a>,\r\n<a href=\"/resources/animations\">Animations</a>,\r\n<a href=\"/resources/teachingmaterials\">Teaching Materials</a>, and more.\r\nThese resources come from <a href=\"/contributors/\">contributors</a> in our\r\nscientific community, and are used by <a href=\"/usage\">visitors from all\r\nover the world</a>.\r\n</p>\r\n<p>\r\nMost importantly, {xhub:getcfg hubShortURL} offers <a href=\"/tools/\">simulation tools</a>\r\nwhich you can access from your web browser, so you can not only learn\r\nabout but also simulate <i>your science area</i>.\r\n</p>\r\n\r\n<h3>Join Us</h3>\r\n<p>\r\n<a href=\"/tour/\" title=\"View demos of this site in action\">Take a tour</a>\r\nof our web site and see how you can use our infrastructure to further your\r\nown research and educational activities.\r\n<a href=\"/register\" title=\"Sign up!\">Create your own account</a>.\r\nIt\'s free and will give you access to our\r\n<a href=\"/tools/\" title=\"View a list of tools\">online simulation tools</a>\r\nand other features.  <a href=\"/contribute\">Become a contributor</a>\r\nby uploading your own presentations and simulation tools for others to share.\r\n<a href=\"/answers/\">Ask a question</a> in our community forum, and let the\r\ncommunity help you out.\r\n</p>','',1,1,0,1,'NOW()',62,'','0000-00-00 00:00:00',1,0,'0000-00-00 00:00:00','NOW()','0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',3,0,2,'','',0,62,'robots=\nauthor=');
