INSERT INTO `#__content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES (9,'Collaboration','collaboration','','<div class=\"threecolumn farleft\">\r\n	<p style=\"padding-top: 4em;\"><a href=\"/groups\"><img src=\"{xhub:image filename=\"collaboration/groups.gif\"}\" alt=\"screen shot\" /></a></p>\r\n</div>\r\n<div class=\"threecolumn middleright\">\r\n	<h3>User Groups</h3>\r\n	<p>Create a <a href=\"/groups\">user group</a> for your research project and invite your colleagues to join.  Upload resources and share them within the group, but keep them private, if you like, or share them with others outside your group.  <a href=\"/groups\">Learn more...</a></p>\r\n	<ul>\r\n	 <li><a href=\"/groups\">View existing groups</a></li>\r\n	 <li><a href=\"/groups/?task=new\">Create your own group</a></li>\r\n	</ul>\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n<div class=\"threecolumn farleft\">\r\n	<p style=\"padding-top: 4em;\"><a href=\"/tools/workspace\"><img src=\"{xhub:image filename=\"collaboration/workspace.gif\"}\" alt=\"screen shot\" /></a></p>\r\n</div>\r\n<div class=\"threecolumn middleright\">\r\n	<h3>Workspaces</h3>\r\n	<p>A <a href=\"/tools/workspace\">workspace</a> is a Linux desktop in your browser. It provides access to NCN\'s <a href=\"http://www.rappture.org\">Rappture toolkit</a>, along with computational resources available on the TeraGrid networks.  You can use these resources to do research.  Or, you can use workspaces as a development area for new tools.  You can upload code, compile it, test it, and debug it.  Once your code is working properly in a workspace, it is ready to be deployed on {xhub:getcfg hubShortName}.</p>\r\n	<div class=\"demoblock\">\r\n		<p><i>NOTE:</i>  This facility is currently restricted to {xhub:getcfg hubShortName} PIs, students, staff, collaborators, and partners.  If you don\'t see <i>Workspace</i> as a tool, you aren\'t yet authorized to use workspaces.  If you feel you should be authorized, please <a href=\"/feedback/report_problems\">contact us</a> to request this feature.  Please include the name of the PI or partnership supporting your request.</p>\r\n	</div>\r\n</div>\r\n<div class=\"clear\"></div>','',0,0,0,0,'NOW()',62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00','NOW()','0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',14,0,3,'','',0,2,'robots=\nauthor=');
INSERT INTO `#__content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES (10,'Support Center','support','','','\r\n{xhub:include type=\"style\" component=\"com_answers\" filename=\"answers.css\"}\r\n\r\n <div class=\"threecolumn farleft\">\r\n			<div id=\"kb\" class=\"mainsection\"><div><div>\r\n				<h3><a href=\"/kb/\">Knowledge Base</a></h3>\r\n 				<p>Have a question or looking for more information about a <a href=\"/kb/tools/\">tool</a> \r\n				or resource? Try browsing our <a href=\"/kb/\">knowledge base</a>! \r\n				You can find answers to frequently asked questions, helpful tips, and any \r\n				other information we thought might be useful.</p>\r\n			</div></div></div>\r\n			\r\n{xhub:module position=\"tcleft\"}\r\n		</div>\r\n\r\n		<div class=\"threecolumn middle\">\r\n			<div id=\"na\" class=\"mainsection\"><div><div>\r\n				<h3><a href=\"answers/\">Answers</a></h3>\r\n				<p>Couldn\'t find an answer to your question in our <a href=\"/kb/\">knowledge base</a>? \r\n				Try asking your fellow hub members! <a href=\"/answers/question/new/\">Ask questions</a>, post answers, or <a href=\"/answers/search/\">search</a> \r\n				for answered questions. You just may find answers to questions you didn\'t \r\n				even know you had.</p>\r\n			</div></div></div>\r\n			\r\n{xhub:module position=\"tcmiddle\"}\r\n		</div>\r\n\r\n		<div class=\"threecolumn farright\">\r\n			<div id=\"rp\" class=\"mainsection\"><div><div>\r\n				<h3><a href=\"/support/report_problems/\">Report Problems</a></h3>\r\n				<p><a href=\"/support/report_problems/\">Report problems</a> with our form and have your problem entered into our <a href=\"/support/tickets/\">ticket tracking \r\n				system</a>. We guarantee a response! After submitting a report you can track its \r\n				progress, even add comments or notes, and we will always notify you of any \r\n				updates!</p>\r\n			</div></div></div>\r\n			\r\n			<h3>Feedback</h3>\r\n			<p>Have an idea for how we can improve? Feel we could be doing something better? \r\n			Send us your suggestions and comments with our feedback form. We want to hear from you!</p>\r\n			<ul>\r\n			 <li><a href=\"/feedback/report_problems/\">Report a problem</a></li>\r\n			 <li><a href=\"/feedback/success_story/\">Share a success story</a></li>\r\n			 <li><a href=\"/feedback/suggestions/\">Send us your suggestions</a></li>\r\n			 <!-- <li><a href=\"/feedback/poll/\">Cast a vote in the latest poll</a></li> -->\r\n			</ul>\r\n		</div>\r\n\r\n		<div class=\"clear\"></div>',1,0,0,0,'NOW()',62,'','0000-00-00 00:00:00',1,0,'0000-00-00 00:00:00','NOW()','0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=0\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',4,0,4,'','',0,0,'robots=\nauthor=');
